sourcesContent:
import dotenv from "dotenv";
import { JWTToken, verifyToken } from "../utils/JWT-Utils";

dotenv.config();

export const varifyToken = async (req, res) => {
  try {

    const token = req.header;
    const role = req.params.role;
    if(!token){
        res.json({ status: false });

    }else{
        const decoded  = verifyToken(token);
        const adminId = decoded.email 
        console.log(decoded.email,decoded.role)

        if (decoded.email === process.env.ADMIN && decoded.role === role) {
             res.status(200).json({ status: true });
          }else{
            res.json({ admin: false });
          }  
    }

  } catch (error) {
    console.log(error);
    return res.status(500).json({ error: error });
  }

};

