const router = Router();

//Books Route
router.get('/fetch', FetchBooks);
router.post('/add',uploadFile, ADDBooks);
router.get('/single', SingleBooks);
router.put('/single', updateBook);
router.delete('/remove', DeleteBooks);

export default router;

