sourcesContent:
import { UserModel } from "../Model/UserModel";
import ErrorResponse from "../utils/Error-Utils";
import { RandomGenerator } from "../utils/RandomGenerator";

export const FetchUser = (req, res, next) => {
  console.log("fetch user");
  try {
    UserModel.find()
      .then((result) => res.json({ result }))
      .catch((err) => {
        console.log(`in the controller ${err}`);
        next(ErrorResponse.internalError(err.message.split(": ")[1]));
      });
  } catch (error) {
    next(error);
  }
};

export const ADDUser = async (req, res, next) => {
  console.log("add user");
  try {
    console.log(req.token);

    const {
      Name,
      Email,
      PhoneNo,
      PinNo,
      Gender,
      Country,
      State,
      City,
      Area,
      LandMark,
      District,
      DOB,
      Post,
      House,
      Occupation,
    } = req.body;
    const LibraryCode = RandomGenerator(6);
    const LibraryId = req.token.id;
    console.log(LibraryCode);

    const existEmail = await UserModel.findOne({ Email });
    console.log(existEmail);
    if (existEmail)
      return next(ErrorResponse.forbidden("Email is already in use"));

    const users = new UserModel({
      Name,
      Email,
      PhoneNo,
      Gender,
      DOB,
      Occupation,
      LibraryCode,
      LibraryId,
      Address: {
        House,
        Post,
        Country,
        State,
        District,
        City,
        Area,
        LandMark,
        PinNo,
      },
    });
    users
      .save()
      .then((result) => res.json({ result }))
      .catch((err) => {
        console.log(`in the controller ${err}`);
        next(ErrorResponse.internalError(err.message.split(": ")[1]));
      });
  } catch (error) {
    next(error);
  }
};

export const SingleUser = (req, res, next) => {
  try {
  } catch (error) {}
};

export const updateUser = (req, res, next) => {
  try {
  } catch (error) {}
};

export const ToggleUser = (req, res, next) => {
  try {
    const { UserId } = req.query;
    UserModel.findById(UserId)
      .then((res) => {
        console.log("user:", res);

        return UserModel.findByIdAndUpdate(
          UserId,
          { isBlocked: !res.isBlocked },
          { new: true }
        );
      })
      .then((updatedUser) => {
        console.log("Updated user:", updatedUser);
        if(updatedUser)res.json({ result: updatedUser })
        else next(ErrorResponse.noDataFound('user'))
      })
      .catch((error) => {
        next(ErrorResponse.internalError(err.message.split(": ")[1]));
      });
  } catch (error) {}
};

