import mongoose from "mongoose";

const PlansForUser = new mongoose.Schema(
  {
    // LibraryId: {
    //   type: mongoose.Schema.Types.ObjectId,
    //   ref: "Library",
    //   required: true,
    // },
    Name: {
      type: String,
      required:true,
    },
    Duration: {
      type: String,
      required:true,
    },
    Amount: {
      type: Number,
      required:true,
    },
  },
  { timestamps: true } // Add the timestamps option to automatically create createdAt and updatedAt fields
);

const LibraryPlanModal = new mongoose.model("LibraryPlan", PlansForUser);
module.exports = LibraryPlanModal;

